import { useState, useEffect } from 'react'

interface Design3DProps {
  projectId: string
  stepId: number
  onSave: (data: any) => void
}

interface CADFile {
  id: string
  name: string
  type: 'stl' | 'obj' | 'step' | 'f3d' | 'other'
  size: number
  uploadDate: string
  version: string
  notes: string
  thumbnail?: string
}

interface DesignData {
  software: string
  files: CADFile[]
  specifications: {
    dimensions: { width: number; height: number; depth: number; unit: string }
    material: string
    color: string
    infillPercentage: number
    layerHeight: number
    supportRequired: boolean
    estimatedPrintTime: number
