import { useState, useEffect } from 'react'

interface CodingProps {
  projectId: string
  stepId: number
  onSave: (data: any) => void
}

interface CodeFile {
  id: string
  name: string
  language: string
  content: string
  lastModified: string
}

interface CodingData {
  platform: 'Arduino' | 'RaspberryPi' | 'ESP32' | 'STM32' | 'Other'
  language: string
  files: CodeFile[]
  libraries: { name: string; version: string; purpose: string }[]
  githubRepo: string
  circuit: {
    components: { name: string; quantity: number; pins: string }[]
    voltage: string
    current: string
    schematicUrl: string
  }
  testResults: { test: string; result: 'pass' | 'fail' | 'pending'; notes: string }[]
}

export function StepCoding({ projectId, stepId, onSave }: CodingProps) {
  const [codingData, setCodingData] = useState<CodingData>({
    platform: 'Arduino',
    language: 'C++',
    files: [],
    libraries: [],
    githubRepo: '',
    circuit: {
      components: [],
      voltage: '5V',
      current: '',
      schematicUrl: ''
    },
    testResults: []
  })

  const [activeFile, setActiveFile] = useState<string | null>(null)
  const [newFile, setNewFile] = useState({ name: '', language: 'C++' })
  const [newLibrary, setNewLibrary] = useState({ name: '', version: '', purpose: '' })
  const [newComponent, setNewComponent] = useState({ name: '', quantity: 1, pins: '' })
  const [newTest, setNewTest] = useState({ test: '', result: 'pending' as const, notes: '' })

  useEffect(() => {
    const saved = localStorage.getItem(`coding-${projectId}-${stepId}`)
    if (saved) {
      setCodingData(JSON.parse(saved))
    }
  }, [projectId, stepId])

  const saveData = (data: CodingData) => {
    setCodingData(data)
    localStorage.setItem(`coding-${projectId}-${stepId}`, JSON.stringify(data))
    onSave(data)
  }

  const createFile = () => {
    if (!newFile.name) return

    const file: CodeFile = {
      id: Date.now().toString(),
      name: newFile.name,
      language: newFile.language,
      content: getStarterCode(codingData.platform, newFile.language),
      lastModified: new Date().toISOString()
    }

    const updated = {
      ...codingData,
      files: [...codingData.files, file]
    }
    saveData(updated)
    setNewFile({ name: '', language: 'C++' })
    setActiveFile(file.id)
  }

  const getStarterCode = (platform: string, language: string) => {
    if (platform === 'Arduino' && language === 'C++') {
      return `// ${platform} Code
// Created: ${new Date().toLocaleDateString()}

void setup() {
  // ì´ˆê¸°í™” ì½”ë“œ
  Serial.begin(9600);

  // í•€ ëª¨ë“œ ì„¤ì •

}

void loop() {
  // ë©”ì¸ ë£¨í”„

  delay(100);
}`
    } else if (platform === 'RaspberryPi' && language === 'Python') {
      return `#!/usr/bin/env python3
# -*- coding: utf-8 -*-
"""
${platform} Control Script
Created: ${new Date().toLocaleDateString()}
"""

import RPi.GPIO as GPIO
import time

def setup():
    """ì´ˆê¸° ì„¤ì •"""
    GPIO.setmode(GPIO.BCM)
    # GPIO í•€ ì„¤ì •

def main():
    """ë©”ì¸ í•¨ìˆ˜"""
    setup()

    try:
        while True:
            # ë©”ì¸ ë£¨í”„

            time.sleep(0.1)

    except KeyboardInterrupt:
        GPIO.cleanup()

if __name__ == "__main__":
    main()`
    }
    return '// ì½”ë“œë¥¼ ì…ë ¥í•˜ì„¸ìš”...'
  }

  const updateFileContent = (fileId: string, content: string) => {
    const updated = {
      ...codingData,
      files: codingData.files.map(f =>
        f.id === fileId 
          ? { ...f, content, lastModified: new Date().toISOString() } 
          : f
      )
    }
    saveData(updated)
  }

  const deleteFile = (fileId: string) => {
    const updated = {
      ...codingData,
      files: codingData.files.filter(f => f.id !== fileId)
    }
    saveData(updated)
    if (activeFile === fileId) setActiveFile(null)
  }

  const addLibrary = () => {
    if (!newLibrary.name) return

    const updated = {
      ...codingData,
      libraries: [...codingData.libraries, newLibrary]
    }
    saveData(updated)
    setNewLibrary({ name: '', version: '', purpose: '' })
  }

  const removeLibrary = (index: number) => {
    const updated = {
      ...codingData,
      libraries: codingData.libraries.filter((_, i) => i !== index)
    }
    saveData(updated)
  }

  const addComponent = () => {
    if (!newComponent.name) return

    const updated = {
      ...codingData,
      circuit: {
        ...codingData.circuit,
        components: [...codingData.circuit.components, newComponent]
      }
    }
    saveData(updated)
    setNewComponent({ name: '', quantity: 1, pins: '' })
  }

  const removeComponent = (index: number) => {
    const updated = {
      ...codingData,
      circuit: {
        ...codingData.circuit,
        components: codingData.circuit.components.filter((_, i) => i !== index)
      }
    }
    saveData(updated)
  }

  const addTest = () => {
    if (!newTest.test) return

    const updated = {
      ...codingData,
      testResults: [...codingData.testResults, newTest]
    }
    saveData(updated)
    setNewTest({ test: '', result: 'pending', notes: '' })
  }

  const updateTestResult = (index: number, result: 'pass' | 'fail' | 'pending') => {
    const updated = {
      ...codingData,
      testResults: codingData.testResults.map((t, i) =>
        i === index ? { ...t, result } : t
      )
    }
    saveData(updated)
  }

  const getPlatformIcon = (platform: string) => {
    switch (platform) {
      case 'Arduino': return 'ğŸ”µ'
      case 'RaspberryPi': return 'ğŸ“'
      case 'ESP32': return 'ğŸ“¡'
      case 'STM32': return 'âš¡'
      default: return 'ğŸ’»'
    }
  }

  const getLanguageIcon = (language: string) => {
    switch (language) {
      case 'C++': return 'âš™ï¸'
      case 'Python': return 'ğŸ'
      case 'JavaScript': return 'ğŸŸ¨'
      case 'C': return 'ğŸ”·'
      default: return 'ğŸ“'
    }
  }

  return (
    <div className="space-y-8">
      {/* í”Œë«í¼ ì„ íƒ */}
      <div className="glass-effect rounded-lg p-6">
        <h3 className="text-xl font-bold mb-4 gradient-text">ğŸ¯ ê°œë°œ í”Œë«í¼</h3>
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
          {['Arduino', 'RaspberryPi', 'ESP32', 'STM32', 'Other'].map(platform => (
            <button
              key={platform}
              onClick={() => saveData({ ...codingData, platform: platform as CodingData['platform'] })}
              className={`p-4 rounded-lg border-2 transition-all ${
                codingData.platform === platform
                  ? 'border-volty-pink bg-volty-pink/10'
                  : 'border-gray-700 bg-gray-900/50 hover:border-gray-600'
              }`}
            >
              <div className="text-2xl mb-2">{getPlatformIcon(platform)}</div>
              <div className="text-sm">{platform}</div>
            </button>
          ))}
        </div>
      </div>

      {/* ì½”ë“œ ì—ë””í„° */}
      <div className="glass-effect rounded-lg p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-bold gradient-text">ğŸ’» ì½”ë“œ ì—ë””í„°</h3>
          <div className="flex items-center space-x-2">
            <input
              value={newFile.name}
              onChange={(e) => setNewFile({ ...newFile, name: e.target.value })}
              className="px-3 py-2 bg-gray-900 border border-gray-600 rounded-lg 
                         focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors"
              placeholder="íŒŒì¼ëª….cpp"
            />
            <select
              value={newFile.language}
              onChange={(e) => setNewFile({ ...newFile, language: e.target.value })}
              className="px-3 py-2 bg-gray-900 border border-gray-600 rounded-lg 
                         focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors"
            >
              <option value="C++">C++</option>
              <option value="C">C</option>
              <option value="Python">Python</option>
              <option value="JavaScript">JavaScript</option>
            </select>
            <button
              onClick={createFile}
              className="px-4 py-2 bg-volty-pink hover:bg-volty-pink/80 rounded-lg transition-colors"
            >
              íŒŒì¼ ìƒì„±
            </button>
          </div>
        </div>

        {/* íŒŒì¼ íƒ­ */}
        {codingData.files.length > 0 && (
          <div className="flex space-x-2 mb-4 overflow-x-auto">
            {codingData.files.map(file => (
              <div
                key={file.id}
                className={`flex items-center space-x-2 px-4 py-2 rounded-t-lg cursor-pointer transition-colors ${
                  activeFile === file.id
                    ? 'bg-gray-800 text-white'
                    : 'bg-gray-900/50 text-gray-400 hover:bg-gray-800'
                }`}
