                <button
                  onClick={(e) => {
                    e.stopPropagation()
                    deleteFile(file.id)
                  }}
                  className="text-red-400 hover:text-red-300 ml-2"
                >
                  âœ•
                </button>
              </div>
            ))}
          </div>
        )}

        {/* ì½”ë“œ ì—ë””í„° ì˜ì—­ */}
        {activeFile && (
          <div className="bg-gray-900 rounded-lg p-4">
            <textarea
              value={codingData.files.find(f => f.id === activeFile)?.content || ''}
              onChange={(e) => updateFileContent(activeFile, e.target.value)}
              className="w-full h-96 px-4 py-3 bg-black text-green-400 font-mono text-sm 
                         rounded-lg focus:outline-none resize-none"
              spellCheck={false}
            />
            <div className="mt-2 text-xs text-gray-500">
              ë§ˆì§€ë§‰ ìˆ˜ì •: {new Date(
                codingData.files.find(f => f.id === activeFile)?.lastModified || ''
              ).toLocaleString()}
            </div>
