const db = require('../config/database');

class Tutor {
  static async create(tutorData) {
    try {
      const [id] = await db('tutors').insert(tutorData).returning('id');
      return await this.findById(id);
    } catch (error) {
      throw new Error(`Error creating tutor: ${error.message}`);
    }
  }

  static async findById(id) {
    try {
      const tutor = await db('tutors')
        .select('tutors.*', 'users.email', 'users.name', 'users.profile_image')
        .leftJoin('users', 'tutors.user_id', 'users.id')
        .where('tutors.id', id)
        .first();

      if (tutor) {
        tutor.languages_spoken = JSON.parse(tutor.languages_spoken || '[]');
        tutor.certifications = JSON.parse(tutor.certifications || '[]');
        tutor.available_times = JSON.parse(tutor.available_times || '{}');
      }

      return tutor;
    } catch (error) {
      throw new Error(`Error finding tutor by ID: ${error.message}`);
    }
  }

  static async findByUserId(userId) {
    try {
      const tutor = await db('tutors')
        .where('user_id', userId)
        .first();

      if (tutor) {
        tutor.languages_spoken = JSON.parse(tutor.languages_spoken || '[]');
        tutor.certifications = JSON.parse(tutor.certifications || '[]');
        tutor.available_times = JSON.parse(tutor.available_times || '{}');
      }

      return tutor;
    } catch (error) {
      throw new Error(`Error finding tutor by user ID: ${error.message}`);
    }
  }

  static async findAll(filters = {}) {
    try {
      let query = db('tutors')
        .select('tutors.*', 'users.email', 'users.name', 'users.profile_image')
        .leftJoin('users', 'tutors.user_id', 'users.id')
        .where('tutors.is_available', true);

      // Apply filters
      if (filters.specialty) {
        query = query.where('tutors.specialty', filters.specialty);
      }

      if (filters.minRate && filters.maxRate) {
        query = query.whereBetween('tutors.hourly_rate', [filters.minRate, filters.maxRate]);
      }

      if (filters.minRating) {
        query = query.where('tutors.rating', '>=', filters.minRating);
      }

      if (filters.location_city) {
        query = query.where('tutors.location_city', filters.location_city);
      }

      if (filters.isVerified) {
        query = query.where('tutors.is_verified', true);
      }

      // Sorting
      if (filters.sortBy === 'rating') {
        query = query.orderBy('tutors.rating', 'desc');
      } else if (filters.sortBy === 'price_low') {
        query = query.orderBy('tutors.hourly_rate', 'asc');
      } else if (filters.sortBy === 'price_high') {
        query = query.orderBy('tutors.hourly_rate', 'desc');
      } else if (filters.sortBy === 'experience') {
        query = query.orderBy('tutors.years_experience', 'desc');
      } else {
        query = query.orderBy('tutors.rating', 'desc');
      }

      // Pagination
      const page = filters.page || 1;
      const limit = filters.limit || 20;
      const offset = (page - 1) * limit;

      query = query.limit(limit).offset(offset);

      const tutors = await query;

      // Parse JSON fields
      return tutors.map(tutor => ({
        ...tutor,
        languages_spoken: JSON.parse(tutor.languages_spoken || '[]'),
        certifications: JSON.parse(tutor.certifications || '[]'),
        available_times: JSON.parse(tutor.available_times || '{}')
      }));
    } catch (error) {
      throw new Error(`Error finding tutors: ${error.message}`);
    }
  }

  static async update(id, updateData) {
    try {
      // Stringify JSON fields if they exist
      if (updateData.languages_spoken && typeof updateData.languages_spoken !== 'string') {
        updateData.languages_spoken = JSON.stringify(updateData.languages_spoken);
      }
      if (updateData.certifications && typeof updateData.certifications !== 'string') {
        updateData.certifications = JSON.stringify(updateData.certifications);
      }
      if (updateData.available_times && typeof updateData.available_times !== 'string') {
        updateData.available_times = JSON.stringify(updateData.available_times);
      }

      await db('tutors')
        .where('id', id)
        .update({
          ...updateData,
          updated_at: db.fn.now()
        });

      return await this.findById(id);
    } catch (error) {
      throw new Error(`Error updating tutor: ${error.message}`);
    }
  }

  static async updateRating(tutorId, newRating) {
    try {
      const tutor = await db('tutors').where('id', tutorId).first();

      if (!tutor) {
        throw new Error('Tutor not found');
      }

      const totalReviews = tutor.total_reviews + 1;
      const avgRating = ((tutor.rating * tutor.total_reviews) + newRating) / totalReviews;

      await db('tutors')
        .where('id', tutorId)
        .update({
          rating: avgRating,
          total_reviews: totalReviews,
          updated_at: db.fn.now()
        });

      return await this.findById(tutorId);
    } catch (error) {
      throw new Error(`Error updating tutor rating: ${error.message}`);
    }
  }

  static async incrementSessionCount(tutorId) {
    try {
      await db('tutors')
        .where('id', tutorId)
        .increment('total_sessions', 1)
        .update({ updated_at: db.fn.now() });

      return await this.findById(tutorId);
    } catch (error) {
      throw new Error(`Error incrementing session count: ${error.message}`);
    }
  }

  static async searchByAvailability(dayOfWeek, timeSlot) {
    try {
      const tutors = await this.findAll({ isAvailable: true });

      return tutors.filter(tutor => {
        const availability = tutor.available_times;
        return availability[dayOfWeek] && availability[dayOfWeek].includes(timeSlot);
      });
    } catch (error) {
      throw new Error(`Error searching tutors by availability: ${error.message}`);
    }
  }

  static async getTopRated(limit = 10) {
    try {
      return await db('tutors')
        .select('tutors.*', 'users.email', 'users.name', 'users.profile_image')
        .leftJoin('users', 'tutors.user_id', 'users.id')
        .where('tutors.is_available', true)
        .where('tutors.is_verified', true)
        .orderBy('tutors.rating', 'desc')
        .limit(limit);
    } catch (error) {
      throw new Error(`Error getting top rated tutors: ${error.message}`);
    }
  }

  static async delete(id) {
    try {
      const deleted = await db('tutors')
        .where('id', id)
        .del();

      return deleted > 0;
    } catch (error) {
      throw new Error(`Error deleting tutor: ${error.message}`);
    }
  }
}

module.exports = Tutor;

