const db = require('../config/database');

class Booking {
  static async create(bookingData) {
    try {
      const [id] = await db('bookings').insert(bookingData).returning('id');
      return await this.findById(id);
    } catch (error) {
      throw new Error(`Error creating booking: ${error.message}`);
    }
  }

  static async findById(id) {
    try {
      const booking = await db('bookings')
        .select(
          'bookings.*',
          'users.name as student_name',
          'users.email as student_email',
          'tutors.id as tutor_id',
          'tutor_users.name as tutor_name',
          'tutor_users.email as tutor_email',
          'tutors.hourly_rate',
          'tutors.specialty'
        )
        .leftJoin('users', 'bookings.student_id', 'users.id')
        .leftJoin('tutors', 'bookings.tutor_id', 'tutors.id')
        .leftJoin('users as tutor_users', 'tutors.user_id', 'tutor_users.id')
        .where('bookings.id', id)
        .first();

      return booking;
    } catch (error) {
      throw new Error(`Error finding booking by ID: ${error.message}`);
    }
  }

  static async findByStudent(studentId, status = null) {
    try {
      let query = db('bookings')
        .select(
          'bookings.*',
          'tutors.id as tutor_id',
          'tutor_users.name as tutor_name',
          'tutor_users.profile_image as tutor_image',
          'tutors.specialty',
          'tutors.rating as tutor_rating'
        )
        .leftJoin('tutors', 'bookings.tutor_id', 'tutors.id')
        .leftJoin('users as tutor_users', 'tutors.user_id', 'tutor_users.id')
        .where('bookings.student_id', studentId)
        .orderBy('bookings.scheduled_at', 'desc');

      if (status) {
        query = query.where('bookings.status', status);
      }

      return await query;
    } catch (error) {
      throw new Error(`Error finding bookings by student: ${error.message}`);
    }
  }

  static async findByTutor(tutorId, status = null) {
    try {
      let query = db('bookings')
        .select(
          'bookings.*',
          'users.name as student_name',
          'users.email as student_email',
          'users.profile_image as student_image'
        )
        .leftJoin('users', 'bookings.student_id', 'users.id')
        .where('bookings.tutor_id', tutorId)
        .orderBy('bookings.scheduled_at', 'desc');

      if (status) {
        query = query.where('bookings.status', status);
      }

      return await query;
    } catch (error) {
      throw new Error(`Error finding bookings by tutor: ${error.message}`);
    }
  }

  static async findUpcoming(userId, userType = 'student') {
    try {
      const now = new Date();
      let query = db('bookings')
        .where('scheduled_at', '>', now)
        .whereIn('status', ['pending', 'confirmed'])
        .orderBy('scheduled_at', 'asc');

      if (userType === 'student') {
        query = query
          .select(
            'bookings.*',
            'tutor_users.name as tutor_name',
            'tutors.specialty'
          )
          .leftJoin('tutors', 'bookings.tutor_id', 'tutors.id')
          .leftJoin('users as tutor_users', 'tutors.user_id', 'tutor_users.id')
          .where('bookings.student_id', userId);
      } else {
        query = query
          .select(
            'bookings.*',
            'users.name as student_name'
          )
          .leftJoin('users', 'bookings.student_id', 'users.id')
          .where('bookings.tutor_id', userId);
      }

      return await query;
    } catch (error) {
      throw new Error(`Error finding upcoming bookings: ${error.message}`);
    }
  }

  static async updateStatus(id, status, additionalData = {}) {
    try {
      const updateData = {
        status,
        updated_at: db.fn.now(),
        ...additionalData
      };

      if (status === 'cancelled') {
        updateData.cancelled_at = db.fn.now();
      }

      await db('bookings')
        .where('id', id)
        .update(updateData);

      return await this.findById(id);
    } catch (error) {
      throw new Error(`Error updating booking status: ${error.message}`);
    }
  }

  static async checkAvailability(tutorId, scheduledAt, durationMinutes = 60) {
    try {
      const startTime = new Date(scheduledAt);
      const endTime = new Date(startTime.getTime() + durationMinutes * 60000);

      const conflictingBookings = await db('bookings')
        .where('tutor_id', tutorId)
        .whereIn('status', ['pending', 'confirmed', 'in_progress'])
        .where(function() {
          this.where(function() {
            this.where('scheduled_at', '<=', startTime)
              .whereRaw('DATE_ADD(scheduled_at, INTERVAL duration_minutes MINUTE) > ?', [startTime]);
          })
          .orWhere(function() {
            this.where('scheduled_at', '<', endTime)
              .where('scheduled_at', '>=', startTime);
          });
        });

      return conflictingBookings.length === 0;
    } catch (error) {
      throw new Error(`Error checking availability: ${error.message}`);
    }
  }

  static async addReview(bookingId, userType, rating, review) {
    try {
      const updateData = {
        updated_at: db.fn.now()
      };

      if (userType === 'student') {
        updateData.student_rating = rating;
        updateData.student_review = review;
      } else {
        updateData.tutor_rating = rating;
        updateData.tutor_review = review;
      }

      await db('bookings')
        .where('id', bookingId)
        .update(updateData);

      const booking = await this.findById(bookingId);

      // Update tutor's overall rating if student reviewed
      if (userType === 'student' && rating) {
        const Tutor = require('./Tutor');
        await Tutor.updateRating(booking.tutor_id, rating);
      }

      return booking;
    } catch (error) {
      throw new Error(`Error adding review: ${error.message}`);
    }
  }

  static async getStats(userId, userType = 'student') {
    try {
      const field = userType === 'student' ? 'student_id' : 'tutor_id';

      const stats = await db('bookings')
        .where(field, userId)
        .select(
          db.raw('COUNT(*) as total_bookings'),
          db.raw('COUNT(CASE WHEN status = "completed" THEN 1 END) as completed_sessions'),
          db.raw('COUNT(CASE WHEN status = "cancelled" THEN 1 END) as cancelled_sessions'),
          db.raw('SUM(CASE WHEN status = "completed" THEN duration_minutes ELSE 0 END) as total_minutes'),
          db.raw('AVG(CASE WHEN ' + (userType === 'student' ? 'tutor_rating' : 'student_rating') + ' IS NOT NULL THEN ' + (userType === 'student' ? 'tutor_rating' : 'student_rating') + ' END) as average_rating')
        )
        .first();

      return {
        totalBookings: stats.total_bookings || 0,
        completedSessions: stats.completed_sessions || 0,
        cancelledSessions: stats.cancelled_sessions || 0,
        totalHours: Math.round((stats.total_minutes || 0) / 60),
        averageRating: parseFloat(stats.average_rating || 0).toFixed(1)
      };
    } catch (error) {
      throw new Error(`Error getting booking stats: ${error.message}`);
    }
  }

  static async delete(id) {
    try {
      const deleted = await db('bookings')
        .where('id', id)
        .del();

      return deleted > 0;
    } catch (error) {
      throw new Error(`Error deleting booking: ${error.message}`);
    }
  }
}

module.exports = Booking;

