        is_verified: true,
        is_available: true,
        location_city: 'Seoul',
        location_region: 'Test'
      });

      const tutors = await Tutor.findAll();
      // Should handle gracefully by defaulting to empty object/array
      expect(tutors[1].available_times).toEqual({});
