    table.enum('status', ['active', 'completed', 'abandoned']).defaultTo('active');
    table.timestamps(true, true);

    // Foreign key
    table.foreign('user_id').references('users.id').onDelete('CASCADE');

    // Indexes
    table.index('user_id');
    table.index('session_id');
    table.index('status');
    table.index('started_at');
  });
};

