        value_per_100won: parseFloat(value),
        is_best_value: false // ì´í›„ ë¡œì§ì—ì„œ ê³„ì‚°
      };
    } catch (error) {
      throw new Error(`Error getting package with value: ${error.message}`);
    }
  }
}

