router.post('/calculate', async (req, res) => {
  try {
    const { sessionPrice, platformFeeRate } = req.body;

    if (!sessionPrice || sessionPrice <= 0) {
      return res.status(400).json({
        success: false,
        message: 'Valid session price is required'
      });
