    switch (requirements.type) {
      case 'session_count':
        return userStats.total_sessions >= requirements.count;

      case 'streak_days':
        return userStats.current_streak >= requirements.count;

      case 'avg_score':
        const scoreField = `avg_${requirements.score_type}_score`;
