import { useState, useEffect } from 'react'
import { MockProject } from '../mocks/mockData'
import { StepProblemAnalysis } from './steps/StepProblemAnalysis'
import { StepIdeaSketch } from './steps/StepIdeaSketch'
import { StepPartsManagement } from './steps/StepPartsManagement'
import { Step3DDesign } from './steps/Step3DDesign'
import { StepCoding } from './steps/StepCoding'

interface WorkflowStepDetailProps {
  project: MockProject
  step: any
  onBack: () => void
  onSave: (stepId: number, data: any) => void
}

interface StepData {
  notes: string
  checklist: { id: string, text: string, completed: boolean }[]
  links: { id: string, title: string, url: string }[]
  files: { id: string, name: string, type: string }[]
}

export function WorkflowStepDetail({ project, step, onBack, onSave }: WorkflowStepDetailProps) {
  const [stepData, setStepData] = useState<StepData>({
    notes: '',
    checklist: [],
    links: [],
    files: []
  })
  const [newChecklistItem, setNewChecklistItem] = useState('')
  const [newLink, setNewLink] = useState({ title: '', url: '' })

  useEffect(() => {
    // ì €ì¥ëœ ë‹¨ê³„ ë°ì´í„° ë¡œë“œ (ì‹¤ì œë¡œëŠ” LocalStorage ë˜ëŠ” APIì—ì„œ)
    const savedData = localStorage.getItem(`step-${project.id}-${step.id}`)
    if (savedData) {
      setStepData(JSON.parse(savedData))
    } else {
      // ë‹¨ê³„ë³„ ê¸°ë³¸ í…œí”Œë¦¿ ì„¤ì •
      setStepData(getDefaultStepData(step.name))
    }
  }, [project.id, step.id])

  const getDefaultStepData = (stepName: string): StepData => {
    switch (stepName) {
      case 'ë¬¸ì œìƒí™©':
        return {
          notes: 'ì–´ë–¤ ë¬¸ì œë¥¼ í•´ê²°í•˜ë ¤ê³  í•˜ë‚˜ìš”?\n\n- êµ¬ì²´ì ì¸ ë¬¸ì œ ìƒí™©:\n- ì™œ ì´ ë¬¸ì œê°€ ì¤‘ìš”í•œê°€ìš”:\n- í˜„ì¬ ì–´ë–¤ ë°©ì‹ìœ¼ë¡œ í•´ê²°í•˜ê³  ìˆë‚˜ìš”:',
          checklist: [
            { id: '1', text: 'ë¬¸ì œ ìƒí™© ëª…í™•íˆ ì •ì˜í•˜ê¸°', completed: false },
            { id: '2', text: 'ë¬¸ì œì˜ ì›ì¸ ë¶„ì„í•˜ê¸°', completed: false },
            { id: '3', text: 'í•´ê²°í•´ì•¼ í•  í•µì‹¬ ìš”ì†Œ íŒŒì•…í•˜ê¸°', completed: false }
          ],
          links: [],
          files: []
        }

      case 'ì•„ì´ë””ì–´':
        return {
          notes: 'ë¬¸ì œ í•´ê²°ì„ ìœ„í•œ ì•„ì´ë””ì–´ë“¤:\n\nğŸ’¡ ì•„ì´ë””ì–´ 1:\n- ì¥ì :\n- ë‹¨ì :\n\nğŸ’¡ ì•„ì´ë””ì–´ 2:\n- ì¥ì :\n- ë‹¨ì :\n\nğŸ’¡ ìµœì¢… ì„ íƒí•œ ì•„ì´ë””ì–´:\n- ì„ íƒ ì´ìœ :',
          checklist: [
            { id: '1', text: 'ë¸Œë ˆì¸ìŠ¤í† ë°ìœ¼ë¡œ ë‹¤ì–‘í•œ ì•„ì´ë””ì–´ ìƒê°í•´ë³´ê¸°', completed: false },
            { id: '2', text: 'ê° ì•„ì´ë””ì–´ì˜ ì¥ë‹¨ì  ë¶„ì„í•˜ê¸°', completed: false },
            { id: '3', text: 'ì‹¤í˜„ ê°€ëŠ¥ì„± ê²€í† í•˜ê¸°', completed: false },
            { id: '4', text: 'ìµœì ì˜ ì•„ì´ë””ì–´ ì„ íƒí•˜ê¸°', completed: false }
          ],
          links: [],
          files: []
        }

      case 'ë¶€í’ˆêµ¬ë§¤':
        return {
          notes: 'í•„ìš”í•œ ë¶€í’ˆ ëª©ë¡:\n\nğŸ”§ í•„ìˆ˜ ë¶€í’ˆ:\n- \n- \n\nâš¡ ì „ì ë¶€í’ˆ:\n- \n- \n\nğŸ› ï¸ ë„êµ¬:\n- \n-',
          checklist: [
            { id: '1', text: 'í•„ìš”í•œ ë¶€í’ˆ ë¦¬ìŠ¤íŠ¸ ì‘ì„±', completed: false },
            { id: '2', text: 'ë¶€í’ˆ ì‚¬ì–‘ ë° ê·œê²© í™•ì¸', completed: false },
            { id: '3', text: 'êµ¬ë§¤ì²˜ ë° ê°€ê²© ì¡°ì‚¬', completed: false },
            { id: '4', text: 'ë¶€í’ˆ ì£¼ë¬¸ ì™„ë£Œ', completed: false }
          ],
          links: [
            { id: '1', title: 'ë„¤ì´ë²„ ì‡¼í•‘', url: 'https://shopping.naver.com' },
            { id: '2', title: 'ì•Œë¦¬ìµìŠ¤í”„ë ˆìŠ¤', url: 'https://aliexpress.com' }
          ],
          files: []
        }

      case '3D_ì„¤ê³„':
        return {
          notes: '3D ì„¤ê³„ ê³„íš:\n\nğŸ“ ì„¤ê³„ ìš”êµ¬ì‚¬í•­:\n- í¬ê¸°:\n- ì¬ì§ˆ:\n- ê¸°ëŠ¥:\n\nğŸ¯ ì„¤ê³„ í¬ì¸íŠ¸:\n- \n- ',
          checklist: [
            { id: '1', text: 'ì„¤ê³„ ìš”êµ¬ì‚¬í•­ ì •ë¦¬', completed: false },
            { id: '2', text: 'ìŠ¤ì¼€ì¹˜ ë° ì¹˜ìˆ˜ ê³„ì‚°', completed: false },
            { id: '3', text: '3D ëª¨ë¸ë§ ì™„ë£Œ', completed: false },
            { id: '4', text: 'ì„¤ê³„ ê²€í†  ë° ìˆ˜ì •', completed: false }
          ],
          links: [
            { id: '1', title: 'Fusion 360', url: 'https://autodesk.com/fusion-360' },
            { id: '2', title: 'Thingiverse', url: 'https://thingiverse.com' }
          ],
          files: []
        }

      default:
        return {
          notes: `${stepName} ë‹¨ê³„ ì‘ì—… ë‚´ìš©:\n\n`,
          checklist: [],
          links: [],
          files: []
        }
    }
  }

  const getStepIcon = (stepName: string) => {
    if (stepName.includes('ë¬¸ì œìƒí™©')) return 'ğŸš¨'
    if (stepName.includes('ì•„ì´ë””ì–´')) return 'ğŸ’¡'
    if (stepName.includes('ë¶€í’ˆ')) return 'ğŸ”§'
    if (stepName.includes('3D_ì„¤ê³„')) return 'ğŸ“'
    if (stepName.includes('3D_í”„ë¦°íŒ…')) return 'ğŸ–¨ï¸'
    if (stepName.includes('ì¡°ë¦½')) return 'ğŸ”©'
    if (stepName.includes('ì½”ë”©')) return 'ğŸ’»'
    if (stepName.includes('ì‹¤íŒ¨')) return 'âŒ'
    if (stepName.includes('í…ŒìŠ¤íŠ¸')) return 'ğŸ§ª'
    if (stepName.includes('ë¹„êµ')) return 'ğŸ“Š'
    return 'ğŸ“‹'
  }

  const handleNotesChange = (notes: string) => {
    const updated = { ...stepData, notes }
    setStepData(updated)
    saveStepData(updated)
  }

  const handleChecklistToggle = (checklistId: string) => {
    const updated = {
      ...stepData,
      checklist: stepData.checklist.map(item =>
        item.id === checklistId ? { ...item, completed: !item.completed } : item
      )
    }
    setStepData(updated)
    saveStepData(updated)
  }

  const handleAddChecklistItem = () => {
    if (!newChecklistItem.trim()) return

    const newItem = {
      id: Date.now().toString(),
      text: newChecklistItem.trim(),
      completed: false
    }

    const updated = {
      ...stepData,
      checklist: [...stepData.checklist, newItem]
    }

    setStepData(updated)
    setNewChecklistItem('')
    saveStepData(updated)
  }

  const handleDeleteChecklistItem = (checklistId: string) => {
    const updated = {
      ...stepData,
      checklist: stepData.checklist.filter(item => item.id !== checklistId)
    }
    setStepData(updated)
    saveStepData(updated)
  }

  const handleAddLink = () => {
    if (!newLink.title.trim() || !newLink.url.trim()) return

    const link = {
      id: Date.now().toString(),
      title: newLink.title.trim(),
      url: newLink.url.trim()
    }

    const updated = {
      ...stepData,
      links: [...stepData.links, link]
    }

    setStepData(updated)
    setNewLink({ title: '', url: '' })
    saveStepData(updated)
  }

  const handleDeleteLink = (linkId: string) => {
    const updated = {
      ...stepData,
      links: stepData.links.filter(link => link.id !== linkId)
    }
    setStepData(updated)
    saveStepData(updated)
  }

  const saveStepData = (data: StepData) => {
    localStorage.setItem(`step-${project.id}-${step.id}`, JSON.stringify(data))

    // ìƒìœ„ ì»´í¬ë„ŒíŠ¸ì— ì €ì¥ ì•Œë¦¼
    onSave(step.id, data)
  }

  const getCompletionPercentage = () => {
    if (stepData.checklist.length === 0) return 0
    const completed = stepData.checklist.filter(item => item.completed).length
    return Math.round((completed / stepData.checklist.length) * 100)
  }

  // ë‹¨ê³„ë³„ ì „ë¬¸ ì»´í¬ë„ŒíŠ¸ ë Œë”ë§
  const renderSpecializedComponent = () => {
    const stepName = step.name.toLowerCase()

    if (stepName.includes('ë¬¸ì œ')) {
      return <StepProblemAnalysis projectId={project.id} stepId={step.id} onSave={(data) => onSave(step.id, data)} />
    } else if (stepName.includes('ì•„ì´ë””ì–´')) {
      return <StepIdeaSketch projectId={project.id} stepId={step.id} onSave={(data) => onSave(step.id, data)} />
    } else if (stepName.includes('ë¶€í’ˆ')) {
      return <StepPartsManagement projectId={project.id} stepId={step.id} onSave={(data) => onSave(step.id, data)} />
    } else if (stepName.includes('3d_ì„¤ê³„') || stepName.includes('ì„¤ê³„')) {
      return <Step3DDesign projectId={project.id} stepId={step.id} onSave={(data) => onSave(step.id, data)} />
    } else if (stepName.includes('ì½”ë”©')) {
      return <StepCoding projectId={project.id} stepId={step.id} onSave={(data) => onSave(step.id, data)} />
    }

    return null
  }

  const hasSpecializedComponent = () => {
    const stepName = step.name.toLowerCase()
    return stepName.includes('ë¬¸ì œ') || 
           stepName.includes('ì•„ì´ë””ì–´') || 
           stepName.includes('ë¶€í’ˆ') || 
           stepName.includes('3d_ì„¤ê³„') || 
           stepName.includes('ì„¤ê³„') ||
           stepName.includes('ì½”ë”©')
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* í—¤ë” */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center space-x-4">
          <button
            onClick={onBack}
            className="p-2 hover:bg-gray-800 rounded-lg transition-colors"
          >
            â† ë’¤ë¡œ
          </button>
          <div>
            <div className="flex items-center space-x-3 mb-2">
              <div className="text-3xl">{getStepIcon(step.name)}</div>
              <h2 className="text-3xl font-bold gradient-text">
                {step.name.replace(/_/g, ' ')}
              </h2>
            </div>
            <p className="text-gray-400">{project.name} - {step.order_index}ë‹¨ê³„</p>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          <div className="text-right">
            <div className="text-sm text-gray-400">ì™„ë£Œìœ¨</div>
            <div className="text-xl font-bold text-volty-pink">
              {getCompletionPercentage()}%
            </div>
          </div>
          <div className={`px-4 py-2 rounded-lg ${
            step.completed 
              ? 'bg-green-500/20 text-green-300' 
              : 'bg-gray-800 text-gray-300'
          }`}>
            {step.completed ? 'âœ… ì™„ë£Œ' : 'â³ ì§„í–‰ì¤‘'}
          </div>
        </div>
      </div>

      {/* ì „ë¬¸ ì»´í¬ë„ŒíŠ¸ê°€ ìˆëŠ” ê²½ìš° í‘œì‹œ */}
      {hasSpecializedComponent() ? (
        renderSpecializedComponent()
      ) : (
        // ì „ë¬¸ ì»´í¬ë„ŒíŠ¸ê°€ ì—†ëŠ” ê²½ìš° ê¸°ë³¸ UI í‘œì‹œ
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* ì™¼ìª½: ì‘ì—… ë…¸íŠ¸ */}
        <div className="glass-effect rounded-lg p-6">
          <h3 className="text-xl font-bold mb-4">ğŸ“ ì‘ì—… ë…¸íŠ¸</h3>
          <textarea
            value={stepData.notes}
            onChange={(e) => handleNotesChange(e.target.value)}
            className="w-full h-96 px-4 py-3 bg-gray-900 border border-gray-600 rounded-lg 
                       focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors resize-none
                       font-mono text-sm leading-relaxed"
            placeholder="ì´ ë‹¨ê³„ì—ì„œ í•´ì•¼ í•  ì‘ì—…ì´ë‚˜ ìƒê°ì„ ììœ ë¡­ê²Œ ì ì–´ë³´ì„¸ìš”..."
          />
          <div className="mt-3 text-xs text-gray-500">
            {stepData.notes.length} ê¸€ì
          </div>
        </div>

        {/* ì˜¤ë¥¸ìª½: ì²´í¬ë¦¬ìŠ¤íŠ¸ & ë§í¬ */}
        <div className="space-y-8">
          {/* ì²´í¬ë¦¬ìŠ¤íŠ¸ */}
          <div className="glass-effect rounded-lg p-6">
            <h3 className="text-xl font-bold mb-4">âœ… ì²´í¬ë¦¬ìŠ¤íŠ¸</h3>

            {/* ì§„í–‰ë¥  ë°” */}
            <div className="mb-6">
              <div className="flex justify-between text-sm mb-2">
                <span>ì§„í–‰ë¥ </span>
                <span>{getCompletionPercentage()}%</span>
              </div>
              <div className="w-full bg-gray-800 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-volty-pink to-volty-purple h-2 rounded-full transition-all"
                  style={{ width: `${getCompletionPercentage()}%` }}
                ></div>
              </div>
            </div>

            {/* ì²´í¬ë¦¬ìŠ¤íŠ¸ í•­ëª©ë“¤ */}
            <div className="space-y-3 mb-4">
              {stepData.checklist.map(item => (
                <div key={item.id} className="flex items-center space-x-3">
                  <button
                    onClick={() => handleChecklistToggle(item.id)}
                    className={`flex-shrink-0 w-5 h-5 rounded border-2 transition-colors ${
                      item.completed
                        ? 'bg-green-500 border-green-500'
                        : 'border-gray-400 hover:border-volty-pink'
                    }`}
                  >
                    {item.completed && (
                      <div className="w-full h-full flex items-center justify-center text-white text-xs">
                        âœ“
                      </div>
                    )}
                  </button>
                  <span className={`flex-1 ${
                    item.completed ? 'line-through text-gray-500' : ''
                  }`}>
                    {item.text}
                  </span>
                  <button
                    onClick={() => handleDeleteChecklistItem(item.id)}
                    className="text-red-400 hover:text-red-300 text-sm"
                  >
                    ğŸ—‘ï¸
                  </button>
                </div>
              ))}
            </div>

            {/* ìƒˆ í•­ëª© ì¶”ê°€ */}
            <div className="flex space-x-2">
              <input
                value={newChecklistItem}
                onChange={(e) => setNewChecklistItem(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAddChecklistItem()}
                className="flex-1 px-3 py-2 bg-gray-900 border border-gray-600 rounded-lg 
                           focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors text-sm"
                placeholder="ìƒˆ í•  ì¼ ì¶”ê°€..."
              />
              <button
                onClick={handleAddChecklistItem}
                className="px-4 py-2 bg-volty-pink hover:bg-volty-pink/80 rounded-lg transition-colors text-sm"
              >
                ì¶”ê°€
              </button>
            </div>
          </div>

          {/* ìœ ìš©í•œ ë§í¬ */}
          <div className="glass-effect rounded-lg p-6">
            <h3 className="text-xl font-bold mb-4">ğŸ”— ìœ ìš©í•œ ë§í¬</h3>

            {/* ë§í¬ ëª©ë¡ */}
            <div className="space-y-3 mb-4">
              {stepData.links.map(link => (
                <div key={link.id} className="flex items-center space-x-3 p-3 bg-gray-900/50 rounded-lg">
                  <div className="flex-1">
                    <div className="font-medium">{link.title}</div>
                    <a 
                      href={link.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="text-sm text-blue-400 hover:text-blue-300 truncate block"
                    >
                      {link.url}
                    </a>
                  </div>
                  <button
                    onClick={() => handleDeleteLink(link.id)}
                    className="text-red-400 hover:text-red-300 text-sm"
                  >
                    ğŸ—‘ï¸
                  </button>
                </div>
              ))}
            </div>

            {/* ìƒˆ ë§í¬ ì¶”ê°€ */}
            <div className="space-y-2">
              <input
                value={newLink.title}
                onChange={(e) => setNewLink({ ...newLink, title: e.target.value })}
                className="w-full px-3 py-2 bg-gray-900 border border-gray-600 rounded-lg 
                           focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors text-sm"
                placeholder="ë§í¬ ì œëª©"
              />
              <div className="flex space-x-2">
                <input
                  value={newLink.url}
                  onChange={(e) => setNewLink({ ...newLink, url: e.target.value })}
                  onKeyPress={(e) => e.key === 'Enter' && handleAddLink()}
                  className="flex-1 px-3 py-2 bg-gray-900 border border-gray-600 rounded-lg 
                             focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors text-sm"
                  placeholder="https://..."
                />
                <button
                  onClick={handleAddLink}
                  className="px-4 py-2 bg-volty-pink hover:bg-volty-pink/80 rounded-lg transition-colors text-sm"
                >
                  ì¶”ê°€
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      )}
    </div>
  )
}

