const STORAGE_KEYS = {
  PROJECTS: 'volty-projects',
  SELECTED_PROJECT: 'volty-selected-project',
  USER_PREFERENCES: 'volty-user-preferences',
  AUTO_SAVE_INTERVAL: 'volty-auto-save-interval'
}

interface UserPreferences {
  autoSave: boolean
  autoSaveInterval: number // ì´ˆ ë‹¨ìœ„
  theme: 'dark' | 'light'
  lastOpenProject?: string
}

export class StorageManager {
  // í”„ë¡œì íŠ¸ ë°ì´í„° ì €ì¥
  static saveProjects(projects: any[]): void {
    try {
      localStorage.setItem(STORAGE_KEYS.PROJECTS, JSON.stringify(projects))
      console.log('í”„ë¡œì íŠ¸ ë°ì´í„° ì €ì¥ë¨:', projects.length, 'ê°œ í”„ë¡œì íŠ¸')
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // í”„ë¡œì íŠ¸ ë°ì´í„° ë¡œë“œ
  static loadProjects(): any[] {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.PROJECTS)
      if (!stored) return []

      const projects = JSON.parse(stored)
      console.log('í”„ë¡œì íŠ¸ ë°ì´í„° ë¡œë“œë¨:', projects.length, 'ê°œ í”„ë¡œì íŠ¸')
      return projects
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', error)
      return []
    }
  }

  // ì„ íƒëœ í”„ë¡œì íŠ¸ ì €ì¥
  static saveSelectedProject(project: any): void {
    try {
      localStorage.setItem(STORAGE_KEYS.SELECTED_PROJECT, JSON.stringify(project))
      this.updateUserPreference('lastOpenProject', project.id)
    } catch (error) {
      console.error('ì„ íƒ í”„ë¡œì íŠ¸ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // ì„ íƒëœ í”„ë¡œì íŠ¸ ë¡œë“œ
  static loadSelectedProject(): any | null {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.SELECTED_PROJECT)
      return stored ? JSON.parse(stored) : null
    } catch (error) {
      console.error('ì„ íƒ í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', error)
      return null
    }
  }

  // ì‚¬ìš©ì ì„¤ì • ì €ì¥
  static saveUserPreferences(preferences: UserPreferences): void {
    try {
      localStorage.setItem(STORAGE_KEYS.USER_PREFERENCES, JSON.stringify(preferences))
    } catch (error) {
      console.error('ì‚¬ìš©ì ì„¤ì • ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // ì‚¬ìš©ì ì„¤ì • ë¡œë“œ
  static loadUserPreferences(): UserPreferences {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.USER_PREFERENCES)
      if (!stored) {
        // ê¸°ë³¸ ì„¤ì •
        return {
          autoSave: true,
          autoSaveInterval: 30, // 30ì´ˆ
          theme: 'dark'
        }
      }
      return JSON.parse(stored)
    } catch (error) {
      console.error('ì‚¬ìš©ì ì„¤ì • ë¡œë“œ ì‹¤íŒ¨:', error)
      return {
        autoSave: true,
        autoSaveInterval: 30,
        theme: 'dark'
      }
    }
  }

  // íŠ¹ì • ì„¤ì •ê°’ë§Œ ì—…ë°ì´íŠ¸
  static updateUserPreference(key: keyof UserPreferences, value: any): void {
    const preferences = this.loadUserPreferences()
    preferences[key] = value
    this.saveUserPreferences(preferences)
  }

  // íŠ¹ì • í”„ë¡œì íŠ¸ì˜ ì§„í–‰ ìƒíƒœ ì €ì¥
  static saveProjectProgress(projectId: string, folders: any[]): void {
    try {
      const progressKey = `volty-progress-${projectId}`
      const progressData = {
        projectId,
        folders,
        lastUpdated: new Date().toISOString()
      }
      localStorage.setItem(progressKey, JSON.stringify(progressData))
      console.log('í”„ë¡œì íŠ¸ ì§„í–‰ìƒíƒœ ì €ì¥ë¨:', projectId)
    } catch (error) {
      console.error('ì§„í–‰ìƒíƒœ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // íŠ¹ì • í”„ë¡œì íŠ¸ì˜ ì§„í–‰ ìƒíƒœ ë¡œë“œ
  static loadProjectProgress(projectId: string): any[] | null {
    try {
      const progressKey = `volty-progress-${projectId}`
      const stored = localStorage.getItem(progressKey)
      if (!stored) return null

      const progressData = JSON.parse(stored)
      console.log('í”„ë¡œì íŠ¸ ì§„í–‰ìƒíƒœ ë¡œë“œë¨:', projectId)
      return progressData.folders
    } catch (error) {
      console.error('ì§„í–‰ìƒíƒœ ë¡œë“œ ì‹¤íŒ¨:', error)
      return null
    }
  }

  // ìŠ¤í¬ë¦½íŠ¸ ë°ì´í„° ì €ì¥
  static saveScript(projectId: string, scriptData: any): void {
    try {
      const scriptKey = `volty-script-${projectId}`
      const data = {
        ...scriptData,
        lastUpdated: new Date().toISOString()
      }
      localStorage.setItem(scriptKey, JSON.stringify(data))
      console.log('ìŠ¤í¬ë¦½íŠ¸ ì €ì¥ë¨:', projectId)
    } catch (error) {
      console.error('ìŠ¤í¬ë¦½íŠ¸ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // ìŠ¤í¬ë¦½íŠ¸ ë°ì´í„° ë¡œë“œ
  static loadScript(projectId: string): any | null {
    try {
      const scriptKey = `volty-script-${projectId}`
      const stored = localStorage.getItem(scriptKey)
      return stored ? JSON.parse(stored) : null
    } catch (error) {
      console.error('ìŠ¤í¬ë¦½íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', error)
      return null
    }
  }

  // ì „ì²´ ë°ì´í„° ë‚´ë³´ë‚´ê¸° (ë°±ì—…ìš©)
  static exportAllData(): string {
    const allData = {
      projects: this.loadProjects(),
      preferences: this.loadUserPreferences(),
      selectedProject: this.loadSelectedProject(),
      exportDate: new Date().toISOString(),
      version: '1.0.0'
    }

    // ëª¨ë“  í”„ë¡œì íŠ¸ì˜ ì§„í–‰ìƒíƒœì™€ ìŠ¤í¬ë¦½íŠ¸ë„ í¬í•¨
    const projects = this.loadProjects()
    const progressData: Record<string, any> = {}
    const scriptData: Record<string, any> = {}

    projects.forEach(project => {
      const progress = this.loadProjectProgress(project.id)
      const script = this.loadScript(project.id)

      if (progress) progressData[project.id] = progress
      if (script) scriptData[project.id] = script
    })

    allData['progressData'] = progressData
    allData['scriptData'] = scriptData

    return JSON.stringify(allData, null, 2)
  }

  // ì „ì²´ ë°ì´í„° ê°€ì ¸ì˜¤ê¸° (ë³µì›ìš©)
  static importAllData(jsonData: string): boolean {
    try {
      const data = JSON.parse(jsonData)

      // í”„ë¡œì íŠ¸ ë°ì´í„° ë³µì›
      if (data.projects) {
        this.saveProjects(data.projects)
      }

      // ì‚¬ìš©ì ì„¤ì • ë³µì›
      if (data.preferences) {
        this.saveUserPreferences(data.preferences)
      }

      // ì§„í–‰ìƒíƒœ ë³µì›
      if (data.progressData) {
        Object.entries(data.progressData).forEach(([projectId, folders]) => {
          this.saveProjectProgress(projectId, folders as any[])
        })
      }

      // ìŠ¤í¬ë¦½íŠ¸ ë³µì›
      if (data.scriptData) {
        Object.entries(data.scriptData).forEach(([projectId, script]) => {
          this.saveScript(projectId, script)
        })
      }

      console.log('ë°ì´í„° ë³µì› ì™„ë£Œ')
      return true
    } catch (error) {
      console.error('ë°ì´í„° ë³µì› ì‹¤íŒ¨:', error)
      return false
    }
  }

  // ìŠ¤í† ë¦¬ì§€ ìš©ëŸ‰ í™•ì¸
  static getStorageUsage(): { used: number, total: number, percentage: number } {
    try {
      let total = 0
      for (let key in localStorage) {
        if (localStorage.hasOwnProperty(key) && key.startsWith('volty-')) {
          total += localStorage[key].length
        }
      }

      // ëŒ€ëµì ì¸ ìµœëŒ€ ìš©ëŸ‰ (ë¸Œë¼ìš°ì €ë§ˆë‹¤ ë‹¤ë¦„, ë³´í†µ 5-10MB)
      const maxStorage = 5 * 1024 * 1024 // 5MB

      return {
        used: total,
        total: maxStorage,
        percentage: Math.round((total / maxStorage) * 100)
      }
    } catch (error) {
      console.error('ìŠ¤í† ë¦¬ì§€ ì‚¬ìš©ëŸ‰ í™•ì¸ ì‹¤íŒ¨:', error)
      return { used: 0, total: 0, percentage: 0 }
