      localStorage.removeItem(`volty-script-${projectId}`)
      console.log('í”„ë¡œì íŠ¸ ë°ì´í„° ì‚­ì œë¨:', projectId)
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ë°ì´í„° ì‚­ì œ ì‹¤íŒ¨:', error)
    }
  }

  // ì „ì²´ ì•± ë°ì´í„° ì´ˆê¸°í™”
  static clearAllData(): void {
    try {
