import { useState, useEffect, useRef } from 'react'

interface IdeaSketchProps {
  projectId: string
  stepId: number
  onSave: (data: any) => void
}

interface SketchData {
  ideas: { id: string; title: string; description: string; score: number }[]
  sketches: { id: string; ideaId: string; dataUrl: string; notes: string }[]
  selectedIdea: string | null
}

export function StepIdeaSketch({ projectId, stepId, onSave }: IdeaSketchProps) {
  const [sketchData, setSketchData] = useState<SketchData>({
    ideas: [],
    sketches: [],
    selectedIdea: null
  })

  const [newIdea, setNewIdea] = useState({ title: '', description: '' })
  const [isDrawing, setIsDrawing] = useState(false)
  const [activeIdeaId, setActiveIdeaId] = useState<string | null>(null)
  const [sketchNotes, setSketchNotes] = useState('')

  const canvasRef = useRef<HTMLCanvasElement>(null)
  const [isCanvasActive, setIsCanvasActive] = useState(false)
  const [brushSize, setBrushSize] = useState(3)
  const [brushColor, setBrushColor] = useState('#ec4899')

  useEffect(() => {
    const saved = localStorage.getItem(`sketch-${projectId}-${stepId}`)
    if (saved) {
      setSketchData(JSON.parse(saved))
    }
  }, [projectId, stepId])

  const saveData = (data: SketchData) => {
    setSketchData(data)
    localStorage.setItem(`sketch-${projectId}-${stepId}`, JSON.stringify(data))
    onSave(data)
  }

  const addIdea = () => {
    if (!newIdea.title.trim()) return
    const idea = {
      id: Date.now().toString(),
      title: newIdea.title,
      description: newIdea.description,
      score: 0
    }
    const updated = { ...sketchData, ideas: [...sketchData.ideas, idea] }
    saveData(updated)
    setNewIdea({ title: '', description: '' })
  }

  const updateIdeaScore = (ideaId: string, score: number) => {
    const updated = {
      ...sketchData,
      ideas: sketchData.ideas.map(idea =>
        idea.id === ideaId ? { ...idea, score } : idea
      )
    }
    saveData(updated)
  }

  const selectBestIdea = (ideaId: string) => {
    const updated = { ...sketchData, selectedIdea: ideaId }
    saveData(updated)
  }

  const deleteIdea = (ideaId: string) => {
    const updated = {
      ...sketchData,
      ideas: sketchData.ideas.filter(i => i.id !== ideaId),
      sketches: sketchData.sketches.filter(s => s.ideaId !== ideaId),
      selectedIdea: sketchData.selectedIdea === ideaId ? null : sketchData.selectedIdea
    }
    saveData(updated)
  }

  // Canvas ê·¸ë¦¬ê¸° í•¨ìˆ˜ë“¤
  const startDrawing = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!canvasRef.current) return
    setIsDrawing(true)
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return

    const rect = canvas.getBoundingClientRect()
    const x = e.clientX - rect.left
    const y = e.clientY - rect.top

    ctx.beginPath()
    ctx.moveTo(x, y)
    ctx.strokeStyle = brushColor
    ctx.lineWidth = brushSize
    ctx.lineCap = 'round'
  }

  const draw = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!isDrawing || !canvasRef.current) return
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return

    const rect = canvas.getBoundingClientRect()
    const x = e.clientX - rect.left
    const y = e.clientY - rect.top

    ctx.lineTo(x, y)
    ctx.stroke()
  }

  const stopDrawing = () => {
    setIsDrawing(false)
  }

  const clearCanvas = () => {
    if (!canvasRef.current) return
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return
    ctx.clearRect(0, 0, canvas.width, canvas.height)
  }

  const saveSketch = () => {
    if (!canvasRef.current || !activeIdeaId) return
    const dataUrl = canvasRef.current.toDataURL()

    const sketch = {
      id: Date.now().toString(),
      ideaId: activeIdeaId,
      dataUrl,
      notes: sketchNotes
    }

    const updated = { ...sketchData, sketches: [...sketchData.sketches, sketch] }
    saveData(updated)

    clearCanvas()
    setSketchNotes('')
    setIsCanvasActive(false)
    setActiveIdeaId(null)
  }

  const deleteSketch = (sketchId: string) => {
    const updated = {
      ...sketchData,
      sketches: sketchData.sketches.filter(s => s.id !== sketchId)
    }
    saveData(updated)
  }

  return (
    <div className="space-y-8">
      {/* ì•„ì´ë””ì–´ ë¸Œë ˆì¸ìŠ¤í† ë° */}
      <div className="glass-effect rounded-lg p-6">
        <h3 className="text-xl font-bold mb-4 gradient-text">ğŸ’¡ ì•„ì´ë””ì–´ ë¸Œë ˆì¸ìŠ¤í† ë°</h3>

        {/* ìƒˆ ì•„ì´ë””ì–´ ì¶”ê°€ */}
        <div className="mb-6 p-4 bg-gray-900/50 rounded-lg">
          <input
            value={newIdea.title}
            onChange={(e) => setNewIdea({ ...newIdea, title: e.target.value })}
            className="w-full px-3 py-2 mb-3 bg-gray-900 border border-gray-600 rounded-lg 
                       focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors"
            placeholder="ì•„ì´ë””ì–´ ì œëª©"
          />
          <textarea
            value={newIdea.description}
            onChange={(e) => setNewIdea({ ...newIdea, description: e.target.value })}
            className="w-full px-3 py-2 mb-3 bg-gray-900 border border-gray-600 rounded-lg 
                       focus:outline-none focus:ring-2 focus:ring-volty-pink transition-colors h-20"
            placeholder="ì•„ì´ë””ì–´ ì„¤ëª… (ì–´ë–»ê²Œ ì‘ë™í•˜ë‚˜ìš”?)"
          />
          <button
            onClick={addIdea}
            className="w-full px-4 py-2 bg-gradient-to-r from-volty-pink to-volty-purple 
                       hover:opacity-80 rounded-lg transition-opacity font-semibold"
          >
            ì•„ì´ë””ì–´ ì¶”ê°€
          </button>
        </div>

        {/* ì•„ì´ë””ì–´ ëª©ë¡ */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {sketchData.ideas.map(idea => (
            <div 
              key={idea.id} 
              className={`p-4 rounded-lg border-2 transition-all ${
                sketchData.selectedIdea === idea.id 
                  ? 'border-green-500 bg-green-500/10' 
                  : 'border-gray-700 bg-gray-900/50'
              }`}
            >
              <div className="flex justify-between items-start mb-2">
                <h4 className="font-bold text-lg">{idea.title}</h4>
                <button 
                  onClick={() => deleteIdea(idea.id)}
                  className="text-red-400 hover:text-red-300"
                >
                  âŒ
                </button>
              </div>

              <p className="text-sm text-gray-300 mb-3">{idea.description}</p>

              {/* í‰ê°€ ì ìˆ˜ */}
              <div className="mb-3">
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm text-gray-400">í‰ê°€ ì ìˆ˜</span>
                  <span className="text-sm font-bold text-volty-pink">{idea.score}/10</span>
                </div>
                <input
                  type="range"
                  min="0"
                  max="10"
                  value={idea.score}
                  onChange={(e) => updateIdeaScore(idea.id, Number(e.target.value))}
                  className="w-full"
                />
              </div>

              {/* ì•¡ì…˜ ë²„íŠ¼ë“¤ */}
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setActiveIdeaId(idea.id)
                    setIsCanvasActive(true)
                  }}
                  className="flex-1 px-3 py-1 bg-volty-purple hover:bg-volty-purple/80 
                             rounded-lg transition-colors text-sm"
                >
                  âœï¸ ìŠ¤ì¼€ì¹˜
                </button>
                {sketchData.selectedIdea !== idea.id && (
                  <button
