import { useState } from 'react'
import { MockProject, isDevelopmentMode } from '../mocks/mockData'

interface ProjectDetailsProps {
  project: MockProject
  onBack: () => void
  onOpenScript: () => void
  onOpenFiles?: () => void
  onOpenFolderDetail?: (folder: any) => void
}

export function ProjectDetails({ project, onBack, onOpenScript, onOpenFiles, onOpenFolderDetail }: ProjectDetailsProps) {
  const [folders, setFolders] = useState(project.folders)
  const [updating, setUpdating] = useState<number | null>(null)

  const handleFolderToggle = async (folderId: number, completed: boolean) => {
    setUpdating(folderId)

    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: ë¡œì»¬ ìƒíƒœë§Œ ì—…ë°ì´íŠ¸
        console.log(`Mock í´ë” ìƒíƒœ ë³€ê²½: ${folderId} -> ${completed}`)
        setFolders(prev => 
          prev.map(folder => 
            folder.id === folderId ? { ...folder, completed } : folder
          )
        )

        // ì§€ì—° ì‹œë®¬ë ˆì´ì…˜
        await new Promise(resolve => setTimeout(resolve, 500))
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.updateFolderStatus(folderId, completed)

        if (result.success) {
          setFolders(prev => 
            prev.map(folder => 
              folder.id === folderId ? { ...folder, completed } : folder
            )
          )
        } else {
          console.error('í´ë” ìƒíƒœ ì—…ë°ì´íŠ¸ ì‹¤íŒ¨:', result.error)
        }
      }
    } catch (error) {
      console.error('í´ë” í† ê¸€ ì˜¤ë¥˜:', error)
    } finally {
      setUpdating(null)
    }
  }
