import { useState, useEffect, useCallback } from 'react'
import { ProjectGrid } from './components/ProjectGrid'
import { ProjectDetails } from './components/ProjectDetails'
import { AIScriptPanel } from './components/AIScriptPanel'
import { FileManager } from './components/FileManager'
import { WorkflowStepDetail } from './components/WorkflowStepDetail'
import { CreateProjectModal } from './components/CreateProjectModal'
import { mockProjects, mockProject, isDevelopmentMode } from './mocks/mockData'
import { StorageManager, AutoSave } from './utils/storage'

function App() {
  const [currentView, setCurrentView] = useState<'projects' | 'details' | 'script' | 'files' | 'workflow-step'>('projects')
  const [selectedProject, setSelectedProject] = useState<any>(null)
  const [projects, setProjects] = useState<any[]>([])
  const [showCreateModal, setShowCreateModal] = useState(false)
  const [loading, setLoading] = useState(false)
  const [selectedStep, setSelectedStep] = useState<any>(null)

  // ì•± ì´ˆê¸°í™”
