    initializeApp()
    return () => {
      // ì»´í¬ë„ŒíŠ¸ ì–¸ë§ˆìš´íŠ¸ ì‹œ ìë™ì €ì¥ ì¤‘ì§€
      AutoSave.stop()
    }
  }, [])

  const initializeApp = async () => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: LocalStorageì—ì„œ í”„ë¡œì íŠ¸ ë¡œë“œ ì‹œë„
