import { useState, useEffect, useCallback } from 'react'
import { ProjectGrid } from './components/ProjectGrid'
import { ProjectDetails } from './components/ProjectDetails'
import { AIScriptPanel } from './components/AIScriptPanel'
import { FileManager } from './components/FileManager'
import { WorkflowStepDetail } from './components/WorkflowStepDetail'
import { CreateProjectModal } from './components/CreateProjectModal'
import { mockProjects, mockProject, isDevelopmentMode } from './mocks/mockData'
import { StorageManager, AutoSave } from './utils/storage'

function App() {
  const [currentView, setCurrentView] = useState<'projects' | 'details' | 'script' | 'files' | 'workflow-step'>('projects')
  const [selectedProject, setSelectedProject] = useState<any>(null)
  const [projects, setProjects] = useState<any[]>([])
  const [showCreateModal, setShowCreateModal] = useState(false)
  const [loading, setLoading] = useState(false)
  const [selectedStep, setSelectedStep] = useState<any>(null)

  // 앱 초기화
  useEffect(() => {
    initializeApp()
    return () => {
      // 컴포넌트 언마운트 시 자동저장 중지
      AutoSave.stop()
    }
  }, [])

  const initializeApp = async () => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // 개발 모드: LocalStorage에서 프로젝트 로드 시도
        const savedProjects = StorageManager.loadProjects()
        if (savedProjects.length > 0) {
          console.log('저장된 프로젝트 로드됨:', savedProjects.length, '개')
          setProjects(savedProjects)
        } else {
          console.log('초기 Mock 데이터 사용')
          setProjects(mockProjects)
          // 초기 데이터를 저장
          StorageManager.saveProjects(mockProjects)
        }

        // 마지막 선택 프로젝트 복원
        const lastSelected = StorageManager.loadSelectedProject()
        if (lastSelected && savedProjects.find(p => p.id === lastSelected.id)) {
          setSelectedProject(lastSelected)
          setCurrentView('details')
        }
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.getProjects()
        if (result.success) {
          setProjects(result.data || [])
        } else {
          console.error('프로젝트 로드 실패:', result.error)
        }
      }

      // 자동 저장 시작
      startAutoSave()
    } catch (error) {
      console.error('앱 초기화 오류:', error)
    } finally {
      setLoading(false)
    }
  }

  const loadProjects = async () => {
    await initializeApp()
  }

  const handleProjectSelect = async (projectId: string) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // 개발 모드: Mock 데이터 사용
        const project = mockProjects.find(p => p.id === projectId) || mockProject
        setSelectedProject(project)
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.getProjectDetails(projectId)
        if (result.success) {
          setSelectedProject(result.data)
        } else {
          console.error('프로젝트 상세 로드 실패:', result.error)
        }
      }
      setCurrentView('details')
    } catch (error) {
      console.error('프로젝트 선택 오류:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleCreateProject = async (projectData: {
    name: string
    problem?: string
    description?: string
  }) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // 개발 모드: Mock 생성
        console.log('Mock 프로젝트 생성:', projectData)
        const newProject = {
          ...mockProject,
          id: `mock-${Date.now()}`,
          name: projectData.name,
          problem: projectData.problem,
          description: projectData.description,
          created_at: new Date().toISOString()
        }
        const updatedProjects = [newProject, ...projects]
        setProjects(updatedProjects)
        // 즉시 저장
        if (isDevelopmentMode()) {
          StorageManager.saveProjects(updatedProjects)
        }
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.createProject(projectData)
        if (result.success) {
          await loadProjects() // 목록 새로고침
        } else {
          console.error('프로젝트 생성 실패:', result.error)
        }
      }
    } catch (error) {
      console.error('프로젝트 생성 오류:', error)
    } finally {
      setLoading(false)
      setShowCreateModal(false)
    }
  }

  const handleBackToProjects = () => {
    setCurrentView('projects')
    setSelectedProject(null)
  }

  const handleOpenScriptPanel = () => {
    setCurrentView('script')
  }

  const handleBackFromScript = () => {
    setCurrentView('details')
  }

  const handleOpenFileManager = () => {
    setCurrentView('files')
  }

  const handleBackFromFiles = () => {
    setCurrentView('details')
  }

  const handleOpenFolderDetail = (folder: any) => {
    setSelectedStep(folder)
    setCurrentView('workflow-step')
  }

  const handleBackFromWorkflowStep = () => {
    setCurrentView('details')
    setSelectedStep(null)
  }

  const handleSaveStepData = (stepId: number, data: any) => {
    // 단계 데이터 저장 시 프로젝트 폴더 상태 업데이트
    if (selectedProject) {
      const updatedProject = {
        ...selectedProject,
        folders: selectedProject.folders.map((folder: any) => 
          folder.id === stepId 
            ? { ...folder, hasData: true }
            : folder
        )
      }
      setSelectedProject(updatedProject)

      // 프로젝트 목록도 업데이트
      setProjects(prev => prev.map(p => 
        p.id === selectedProject.id ? updatedProject : p
      ))

      // 즉시 저장
      if (isDevelopmentMode()) {
        StorageManager.saveProjects(projects.map(p => 
          p.id === selectedProject.id ? updatedProject : p
        ))
      }
    }
  }

  // 자동 저장 시작
  const startAutoSave = useCallback(() => {
    const saveCallback = () => {
      if (isDevelopmentMode()) {
        // 프로젝트 목록 저장
        StorageManager.saveProjects(projects)

        // 선택된 프로젝트 저장
        if (selectedProject) {
          StorageManager.saveSelectedProject(selectedProject)
        }

        console.log('💾 자동 저장 완료')
      }
    }

    AutoSave.addCallback(saveCallback)
    AutoSave.start()
  }, [projects, selectedProject])

  const handleDeleteProject = async (projectId: string) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // 개발 모드: 로컬 상태에서 삭제
        console.log('Mock 프로젝트 삭제:', projectId)
        const updatedProjects = projects.filter(p => p.id !== projectId)
        setProjects(updatedProjects)
        // 즉시 저장 및 관련 데이터 삭제
        StorageManager.saveProjects(updatedProjects)
        StorageManager.clearProjectData(projectId)

        // 현재 선택된 프로젝트가 삭제되면 홈으로
        if (selectedProject?.id === projectId) {
          setSelectedProject(null)
          setCurrentView('projects')
        }
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.deleteProject(projectId)
        if (result.success) {
          await loadProjects() // 목록 새로고침

          // 현재 선택된 프로젝트가 삭제되면 홈으로
          if (selectedProject?.id === projectId) {
            setSelectedProject(null)
            setCurrentView('projects')
          }
        } else {
          console.error('프로젝트 삭제 실패:', result.error)
        }
      }
    } catch (error) {
      console.error('프로젝트 삭제 오류:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleDuplicateProject = async (projectId: string) => {
    setLoading(true)
    try {
      const originalProject = projects.find(p => p.id === projectId)
      if (!originalProject) {
        console.error('복사할 프로젝트를 찾을 수 없습니다.')
        return
      }

      if (isDevelopmentMode()) {
        // 개발 모드: 로컬 상태에 복사본 추가
        console.log('Mock 프로젝트 복사:', projectId)
        const duplicatedProject = {
          ...originalProject,
          id: `copy-${Date.now()}`,
          name: `${originalProject.name} (복사본)`,
          created_at: new Date().toISOString(),
          folders: originalProject.folders.map(folder => ({
            ...folder,
            completed: false // 복사본은 모든 단계 초기화
          }))
        }
        const updatedProjects = [duplicatedProject, ...projects]
        setProjects(updatedProjects)
        // 즉시 저장
        StorageManager.saveProjects(updatedProjects)
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.duplicateProject(projectId)
        if (result.success) {
          await loadProjects() // 목록 새로고침
        } else {
          console.error('프로젝트 복사 실패:', result.error)
        }
      }
    } catch (error) {
      console.error('프로젝트 복사 오류:', error)
    } finally {
      setLoading(false)
    }
  }

  // 메인 렌더링
  const renderCurrentView = () => {
    if (loading) {
      return (
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-2 border-volty-pink border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-gray-400">로딩 중...</p>
          </div>
        </div>
      )
    }

    switch (currentView) {
      case 'projects':
        return (
          <ProjectGrid
            projects={projects}
            onProjectSelect={handleProjectSelect}
            onCreateProject={() => setShowCreateModal(true)}
            onDeleteProject={handleDeleteProject}
            onDuplicateProject={handleDuplicateProject}
          />
        )

      case 'details':
        if (!selectedProject) {
          return <div>프로젝트를 선택해주세요.</div>
        }
        return (
          <ProjectDetails
            project={selectedProject}
            onBack={handleBackToProjects}
            onOpenScript={handleOpenScriptPanel}
            onOpenFiles={handleOpenFileManager}
            onOpenFolderDetail={handleOpenFolderDetail}
          />
        )

      case 'script':
        if (!selectedProject) {
          return <div>프로젝트를 선택해주세요.</div>
        }
        return (
          <AIScriptPanel
            project={selectedProject}
            onBack={handleBackFromScript}
          />
        )

      case 'files':
        if (!selectedProject) {
          return <div>프로젝트를 선택해주세요.</div>
        }
        return (
          <FileManager
            project={selectedProject}
            onBack={handleBackFromFiles}
          />
        )

      case 'workflow-step':
        if (!selectedProject || !selectedStep) {
          return <div>단계를 선택해주세요.</div>
        }
        return (
          <WorkflowStepDetail
            project={selectedProject}
            step={selectedStep}
            onBack={handleBackFromWorkflowStep}
            onSave={handleSaveStepData}
          />
        )

      default:
        return <div>알 수 없는 뷰입니다.</div>
    }
  }

  return (
    <div className="min-h-screen bg-volty-dark text-white">
      {/* 앱 헤더 */}
      <header className="border-b border-gray-800 p-4 glass-effect">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-8 h-8 bg-gradient-to-r from-volty-pink to-volty-purple rounded-lg"></div>
            <h1 className="text-2xl font-bold gradient-text">
              Volty Creator Studio
            </h1>
          </div>

          {/* 현재 뷰 표시 */}
          <div className="flex items-center space-x-2 text-sm text-gray-400">
            {currentView === 'projects' && '📁 프로젝트'}
            {currentView === 'details' && selectedProject && (
              <span>🎬 {selectedProject.name}</span>
            )}
            {currentView === 'script' && selectedProject && (
              <span>✍️ {selectedProject.name} - AI 대본</span>
            )}
            {currentView === 'files' && selectedProject && (
              <span>📁 {selectedProject.name} - 파일 관리</span>
            )}
            {currentView === 'workflow-step' && selectedProject && selectedStep && (
              <span>🔧 {selectedProject.name} - {selectedStep.name.replace(/_/g, ' ')}</span>
            )}

            {isDevelopmentMode() && (
              <span className="px-2 py-1 bg-yellow-600 text-yellow-100 text-xs rounded">
                개발모드
              </span>
            )}
          </div>
        </div>
      </header>

      {/* 메인 콘텐츠 */}
      <main className="p-6">
        {renderCurrentView()}
      </main>

      {/* 프로젝트 생성 모달 */}
      {showCreateModal && (
        <CreateProjectModal
          onCreate={handleCreateProject}
          onClose={() => setShowCreateModal(false)}
          loading={loading}
        />
      )}
    </div>
  )
}

export default App

import { useState } from 'react'
import { MockProject, isDevelopmentMode } from '../mocks/mockData'

interface ProjectDetailsProps {
  project: MockProject
  onBack: () => void
  onOpenScript: () => void
  onOpenFiles?: () => void
  onOpenFolderDetail?: (folder: any) => void
}

export function ProjectDetails({ project, onBack, onOpenScript, onOpenFiles, onOpenFolderDetail }: ProjectDetailsProps) {
  const [folders, setFolders] = useState(project.folders)
  const [updating, setUpdating] = useState<number | null>(null)

  const handleFolderToggle = async (folderId: number, completed: boolean) => {
    setUpdating(folderId)

    try {
      if (isDevelopmentMode()) {
        // 개발 모드: 로컬 상태만 업데이트
        console.log(`Mock 폴더 상태 변경: ${folderId} -> ${completed}`)
        setFolders(prev => 
          prev.map(folder => 
            folder.id === folderId ? { ...folder, completed } : folder
          )
        )

        // 지연 시뮬레이션
        await new Promise(resolve => setTimeout(resolve, 500))
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.updateFolderStatus(folderId, completed)

        if (result.success) {
          setFolders(prev => 
            prev.map(folder => 
              folder.id === folderId ? { ...folder, completed } : folder
            )
          )
        } else {
          console.error('폴더 상태 업데이트 실패:', result.error)
        }
      }
    } catch (error) {
      console.error('폴더 토글 오류:', error)
    } finally {
      setUpdating(null)
    }
  }

  const getCompletedCount = () => {
    return folders.filter(f => f.completed).length
  }

  const getProgressPercentage = () => {
    return Math.round((getCompletedCount() / folders.length) * 100)
  }

  const getFolderEmoji = (folderName: string) => {
    if (folderName.includes('문제상황')) return '🚨'
    if (folderName.includes('아이디어')) return '💡'
    if (folderName.includes('부품')) return '🔧'
    if (folderName.includes('3D_설계')) return '📐'
    if (folderName.includes('3D_프린팅')) return '🖨️'
    if (folderName.includes('조립')) return '🔩'
    if (folderName.includes('코딩')) return '💻'
    if (folderName.includes('실패')) return '❌'
    if (folderName.includes('테스트')) return '🧪'
    if (folderName.includes('비교')) return '📊'
    return '📁'
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* 상단 네비게이션 */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center space-x-4">
          <button
            onClick={onBack}
            className="p-2 hover:bg-gray-800 rounded-lg transition-colors"
          >
            ← 뒤로
          </button>
          <div>
            <h2 className="text-3xl font-bold">{project.name}</h2>
            <p className="text-gray-400 mt-1">{project.problem}</p>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={onOpenScript}
            className="px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded-lg transition-colors"
          >
            ✍️ AI 대본 생성
          </button>
          {onOpenFiles && (
            <button
              onClick={onOpenFiles}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
            >
              📁 파일 관리
            </button>
          )}
          <div className="text-right">
            <div className="text-sm text-gray-400">진행률</div>
            <div className="text-xl font-bold text-volty-pink">
              {getProgressPercentage()}%
            </div>
          </div>
        </div>
      </div>

      {/* 프로젝트 정보 카드 */}
      <div className="glass-effect rounded-lg p-6 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <h3 className="font-semibold mb-2">프로젝트 상태</h3>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
              <span>📹 촬영중</span>
            </div>
          </div>

          <div>
            <h3 className="font-semibold mb-2">생성일</h3>
            <p className="text-gray-400">
              {new Date(project.created_at).toLocaleDateString('ko-KR')}
            </p>
          </div>

          <div>
            <h3 className="font-semibold mb-2">완료된 단계</h3>
            <div className="flex items-center space-x-3">
              <div className="flex-1 bg-gray-800 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-volty-pink to-volty-purple h-2 rounded-full transition-all"
                  style={{ width: `${getProgressPercentage()}%` }}
                ></div>
              </div>
              <span className="text-volty-pink font-semibold">
                {getCompletedCount()}/10
              </span>
            </div>
          </div>
        </div>

        {project.description && (
          <div className="mt-6 pt-6 border-t border-gray-700">
            <h3 className="font-semibold mb-2">프로젝트 설명</h3>
            <p className="text-gray-300">{project.description}</p>
          </div>
        )}
      </div>

      {/* 🔥 테스트 버튼 - 문제 진단용 */}
      <div className="glass-effect rounded-lg p-6 mb-6 border-2 border-yellow-500">
        <h3 className="text-xl font-bold mb-4">🔧 디버깅 테스트</h3>
        <div className="space-y-3">
          <button 
            onClick={() => {
              console.log('📍 테스트 1: onOpenFolderDetail 함수 존재 여부:', !!onOpenFolderDetail)
              console.log('📍 테스트 2: folders 데이터:', folders)
              alert(`onOpenFolderDetail 함수: ${onOpenFolderDetail ? '존재함 ✅' : '없음 ❌'}`)
            }}
            className="px-4 py-2 bg-yellow-600 hover:bg-yellow-700 rounded-lg text-white font-semibold mr-3"
          >
            1. 함수 확인
          </button>

          <button 
            onClick={() => {
              if (onOpenFolderDetail && folders[0]) {
                console.log('📍 테스트 3: 첫 번째 폴더로 이동 시도')
                onOpenFolderDetail(folders[0])
              } else {
                alert('함수나 폴더가 없습니다!')
              }
            }}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 rounded-lg text-white font-semibold"
          >
            2. 첫 번째 단계 열기
          </button>
        </div>
        <p className="text-sm text-yellow-400 mt-3">위 버튼들을 눌러서 문제를 진단해보세요!</p>
      </div>

      {/* 워크플로우 폴더 그리드 */}
      <div className="glass-effect rounded-lg p-6">
        <h3 className="text-xl font-bold mb-6">워크플로우 단계</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
          {folders.map((folder, index) => (
            <div
              key={folder.id}
              className={`relative rounded-lg border-2 transition-all folder-item ${
                folder.completed
                  ? 'bg-green-500/10 border-green-500'
                  : 'bg-gray-800/50 border-gray-600'
              }`}
              style={{ '--item-index': index } as any}
            >
              {/* 체크박스 - 우측 상단 고정 */}
              <div className="absolute top-3 right-3 z-10">
                {updating === folder.id ? (
                  <div className="w-5 h-5 border-2 border-volty-pink border-t-transparent rounded-full animate-spin"></div>
                ) : (
                  <button
                    onClick={(e) => {
                      e.stopPropagation()
                      handleFolderToggle(folder.id, !folder.completed)
                    }}
                    className={`w-5 h-5 rounded-full border-2 transition-colors hover:scale-110 shadow-lg ${
                      folder.completed
                        ? 'bg-green-500 border-green-500'
                        : 'bg-gray-800 border-gray-400 hover:border-volty-pink'
                    }`}
                    title="완료 상태 변경"
                  >
                    {folder.completed && (
                      <div className="w-full h-full flex items-center justify-center text-white text-xs">
                        ✓
                      </div>
                    )}
                  </button>
                )}
              </div>

              {/* 메인 클릭 영역 - 체크박스 제외한 전체 */}
              <div 
                className="p-4 cursor-pointer hover:bg-gray-700/30 transition-colors rounded-lg"
                onClick={() => {
                  console.log('박스 클릭됨:', folder.name, folder)
                  alert(`${folder.name} 단계 클릭됨!`)
                  onOpenFolderDetail?.(folder)
                }}
              >
                {/* 폴더 아이콘 */}
                <div className="flex items-center mb-3">
                  <div className="text-2xl mr-3">
                    {getFolderEmoji(folder.name)}
                  </div>
                  <div className="text-xs text-gray-400">클릭하여 작업하기</div>
                </div>

                {/* 폴더 정보 */}
                <div>
                  <h4 className="font-medium text-sm mb-2 leading-tight">
                    {folder.name.replace(/_/g, ' ')}
                  </h4>
                  <div className="text-xs text-gray-400">
                    {folder.video_count > 0 ? (
                      `🎥 ${folder.video_count}개 영상`
                    ) : (
                      '영상 없음'
                    )}
                  </div>

                  {/* 완료 상태 */}
                  <div className={`mt-2 text-xs px-2 py-1 rounded ${
                    folder.completed
                      ? 'bg-green-500/20 text-green-300'
                      : 'bg-gray-700 text-gray-400'
                  }`}>
                    {folder.completed ? '✅ 완료' : '⏳ 대기중'}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* 하단 안내 */}
        <div className="mt-8 p-4 bg-gray-900/50 rounded-lg">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-400">
            <div>
              <h4 className="font-medium text-volty-pink mb-2">📝 상세 작업하기</h4>
              <p>단계 박스를 클릭하면 상세 작업 화면이 열립니다.</p>
              <ul className="mt-2 space-y-1">
                <li>• 작업 노트 작성</li>
                <li>• 체크리스트 관리</li>
                <li>• 참고 링크 저장</li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium text-volty-purple mb-2">✅ 완료 상태 변경</h4>
              <p>우측 상단의 동그란 버튼을 클릭하면 완료 상태를 변경할 수 있습니다.</p>
              <p className="mt-2">모든 단계 완료 시 AI 대본 생성이 활성화됩니다.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

