import { useState } from 'react'
import { MockProject, isDevelopmentMode } from '../mocks/mockData'

interface ProjectDetailsProps {
  project: MockProject
  onBack: () => void
  onOpenScript: () => void
  onOpenFiles?: () => void
  onOpenFolderDetail?: (folder: any) => void
}

export function ProjectDetails({ project, onBack, onOpenScript, onOpenFiles, onOpenFolderDetail }: ProjectDetailsProps) {
  const [folders, setFolders] = useState(project.folders)
  const [updating, setUpdating] = useState<number | null>(null)

  const handleFolderToggle = async (folderId: number, completed: boolean) => {
    setUpdating(folderId)

    try {
      if (isDevelopmentMode()) {
        // 개발 모드: 로컬 상태만 업데이트
        console.log(`Mock 폴더 상태 변경: ${folderId} -> ${completed}`)
        setFolders(prev => 
          prev.map(folder => 
            folder.id === folderId ? { ...folder, completed } : folder
          )
        )

        // 지연 시뮬레이션
        await new Promise(resolve => setTimeout(resolve, 500))
      } else {
        // 프로덕션 모드: Electron API 사용
        const result = await window.electronAPI.updateFolderStatus(folderId, completed)

        if (result.success) {
          setFolders(prev => 
            prev.map(folder => 
              folder.id === folderId ? { ...folder, completed } : folder
            )
          )
        } else {
          console.error('폴더 상태 업데이트 실패:', result.error)
        }
      }
    } catch (error) {
      console.error('폴더 토글 오류:', error)
    } finally {
      setUpdating(null)
    }
  }

  const getCompletedCount = () => {
    return folders.filter(f => f.completed).length
  }

  const getProgressPercentage = () => {
    return Math.round((getCompletedCount() / folders.length) * 100)
  }

  const getFolderEmoji = (folderName: string) => {
    if (folderName.includes('문제상황')) return '🚨'
    if (folderName.includes('아이디어')) return '💡'
    if (folderName.includes('부품')) return '🔧'
    if (folderName.includes('3D_설계')) return '📐'
    if (folderName.includes('3D_프린팅')) return '🖨️'
    if (folderName.includes('조립')) return '🔩'
    if (folderName.includes('코딩')) return '💻'
    if (folderName.includes('실패')) return '❌'
    if (folderName.includes('테스트')) return '🧪'
    if (folderName.includes('비교')) return '📊'
    return '📁'
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* 상단 네비게이션 */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center space-x-4">
          <button
            onClick={onBack}
            className="p-2 hover:bg-gray-800 rounded-lg transition-colors"
          >
            ← 뒤로
          </button>
          <div>
            <h2 className="text-3xl font-bold">{project.name}</h2>
            <p className="text-gray-400 mt-1">{project.problem}</p>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={onOpenScript}
            className="px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded-lg transition-colors"
          >
            ✍️ AI 대본 생성
          </button>
          {onOpenFiles && (
            <button
              onClick={onOpenFiles}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
            >
              📁 파일 관리
            </button>
          )}
          <div className="text-right">
            <div className="text-sm text-gray-400">진행률</div>
            <div className="text-xl font-bold text-volty-pink">
              {getProgressPercentage()}%
            </div>
          </div>
        </div>
      </div>

      {/* 프로젝트 정보 카드 */}
      <div className="glass-effect rounded-lg p-6 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <h3 className="font-semibold mb-2">프로젝트 상태</h3>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
              <span>📹 촬영중</span>
            </div>
          </div>

          <div>
            <h3 className="font-semibold mb-2">생성일</h3>
            <p className="text-gray-400">
              {new Date(project.created_at).toLocaleDateString('ko-KR')}
            </p>
          </div>

          <div>
            <h3 className="font-semibold mb-2">완료된 단계</h3>
            <div className="flex items-center space-x-3">
              <div className="flex-1 bg-gray-800 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-volty-pink to-volty-purple h-2 rounded-full transition-all"
                  style={{ width: `${getProgressPercentage()}%` }}
                ></div>
              </div>
              <span className="text-volty-pink font-semibold">
                {getCompletedCount()}/10
              </span>
            </div>
          </div>
        </div>

        {project.description && (
          <div className="mt-6 pt-6 border-t border-gray-700">
            <h3 className="font-semibold mb-2">프로젝트 설명</h3>
            <p className="text-gray-300">{project.description}</p>
          </div>
        )}
      </div>

      {/* 🔥 테스트 버튼 - 문제 진단용 */}
      <div className="glass-effect rounded-lg p-6 mb-6 border-2 border-yellow-500">
        <h3 className="text-xl font-bold mb-4">🔧 디버깅 테스트</h3>
        <div className="space-y-3">
          <button 
            onClick={() => {
              console.log('📍 테스트 1: onOpenFolderDetail 함수 존재 여부:', !!onOpenFolderDetail)
              console.log('📍 테스트 2: folders 데이터:', folders)
              alert(`onOpenFolderDetail 함수: ${onOpenFolderDetail ? '존재함 ✅' : '없음 ❌'}`)
            }}
            className="px-4 py-2 bg-yellow-600 hover:bg-yellow-700 rounded-lg text-white font-semibold mr-3"
          >
            1. 함수 확인
          </button>

          <button 
            onClick={() => {
              if (onOpenFolderDetail && folders[0]) {
                console.log('📍 테스트 3: 첫 번째 폴더로 이동 시도')
                onOpenFolderDetail(folders[0])
              } else {
                alert('함수나 폴더가 없습니다!')
              }
            }}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 rounded-lg text-white font-semibold"
          >
            2. 첫 번째 단계 열기
          </button>
        </div>
        <p className="text-sm text-yellow-400 mt-3">위 버튼들을 눌러서 문제를 진단해보세요!</p>
      </div>

      {/* 워크플로우 폴더 그리드 */}
      <div className="glass-effect rounded-lg p-6">
        <h3 className="text-xl font-bold mb-6">워크플로우 단계</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
          {folders.map((folder, index) => (
            <div
              key={folder.id}
              className={`relative rounded-lg border-2 transition-all folder-item ${
                folder.completed
                  ? 'bg-green-500/10 border-green-500'
                  : 'bg-gray-800/50 border-gray-600'
              }`}
              style={{ '--item-index': index } as any}
            >
              {/* 체크박스 - 우측 상단 고정 */}
              <div className="absolute top-3 right-3 z-10">
                {updating === folder.id ? (
                  <div className="w-5 h-5 border-2 border-volty-pink border-t-transparent rounded-full animate-spin"></div>
                ) : (
                  <button
                    onClick={(e) => {
                      e.stopPropagation()
                      handleFolderToggle(folder.id, !folder.completed)
                    }}
                    className={`w-5 h-5 rounded-full border-2 transition-colors hover:scale-110 shadow-lg ${
                      folder.completed
                        ? 'bg-green-500 border-green-500'
                        : 'bg-gray-800 border-gray-400 hover:border-volty-pink'
                    }`}
                    title="완료 상태 변경"
                  >
                    {folder.completed && (
                      <div className="w-full h-full flex items-center justify-center text-white text-xs">
                        ✓
                      </div>
                    )}
                  </button>
                )}
              </div>

              {/* 메인 클릭 영역 - 체크박스 제외한 전체 */}
              <div 
                className="p-4 cursor-pointer hover:bg-gray-700/30 transition-colors rounded-lg"
                onClick={() => {
                  console.log('박스 클릭됨:', folder.name, folder)
                  alert(`${folder.name} 단계 클릭됨!`)
                  onOpenFolderDetail?.(folder)
                }}
              >
                {/* 폴더 아이콘 */}
                <div className="flex items-center mb-3">
                  <div className="text-2xl mr-3">
                    {getFolderEmoji(folder.name)}
                  </div>
                  <div className="text-xs text-gray-400">클릭하여 작업하기</div>
                </div>

                {/* 폴더 정보 */}
                <div>
                  <h4 className="font-medium text-sm mb-2 leading-tight">
                    {folder.name.replace(/_/g, ' ')}
                  </h4>
                  <div className="text-xs text-gray-400">
                    {folder.video_count > 0 ? (
                      `🎥 ${folder.video_count}개 영상`
                    ) : (
                      '영상 없음'
                    )}
                  </div>

                  {/* 완료 상태 */}
                  <div className={`mt-2 text-xs px-2 py-1 rounded ${
                    folder.completed
                      ? 'bg-green-500/20 text-green-300'
                      : 'bg-gray-700 text-gray-400'
                  }`}>
                    {folder.completed ? '✅ 완료' : '⏳ 대기중'}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* 하단 안내 */}
        <div className="mt-8 p-4 bg-gray-900/50 rounded-lg">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-400">
            <div>
              <h4 className="font-medium text-volty-pink mb-2">📝 상세 작업하기</h4>
              <p>단계 박스를 클릭하면 상세 작업 화면이 열립니다.</p>
              <ul className="mt-2 space-y-1">
                <li>• 작업 노트 작성</li>
                <li>• 체크리스트 관리</li>
                <li>• 참고 링크 저장</li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium text-volty-purple mb-2">✅ 완료 상태 변경</h4>
              <p>우측 상단의 동그란 버튼을 클릭하면 완료 상태를 변경할 수 있습니다.</p>
              <p className="mt-2">모든 단계 완료 시 AI 대본 생성이 활성화됩니다.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

