        # ì²˜ìŒ 5ê°œ ì²´í¬ë°•ìŠ¤ì˜ êµ¬ì¡° ë¶„ì„
        for i, checkbox in enumerate(checkboxes[:5]):
            print(f"\n{Fore.YELLOW}ì²´í¬ë°•ìŠ¤ #{i+1} êµ¬ì¡°:{Style.RESET_ALL}")

            try:
                # ë¶€ëª¨ ìš”ì†Œë“¤ ì¶”ì 
                parent = checkbox.find_element(By.XPATH, "..")
                grandparent = parent.find_element(By.XPATH, "..")
                greatgrandparent = grandparent.find_element(By.XPATH, "..")

                print(f"  ì²´í¬ë°•ìŠ¤ íƒœê·¸: {checkbox.tag_name}")
                print(f"  ë¶€ëª¨ íƒœê·¸: {parent.tag_name} (class: {parent.get_attribute('class')})")
                print(f"  ì¡°ë¶€ëª¨ íƒœê·¸: {grandparent.tag_name} (class: {grandparent.get_attribute('class')})")
                print(f"  ì¦ì¡°ë¶€ëª¨ íƒœê·¸: {greatgrandparent.tag_name}")

                # ê°™ì€ í–‰ì˜ ëª¨ë“  ìš”ì†Œ
                if grandparent.tag_name == 'tr' or parent.tag_name == 'tr':
                    row = grandparent if grandparent.tag_name == 'tr' else parent
                    cells = row.find_elements(By.TAG_NAME, "td")
                    print(f"\n  ê°™ì€ í–‰ì˜ ì…€ ê°œìˆ˜: {len(cells)}ê°œ")
                    for j, cell in enumerate(cells):
                        text = cell.text.strip()
                        if text:
                            print(f"    ì…€ {j+1}: '{text[:30]}...' " if len(text) > 30 else f"    ì…€ {j+1}: '{text}'")

                # í˜•ì œ ìš”ì†Œë“¤
                siblings = parent.find_elements(By.XPATH, "../*")
                print(f"\n  í˜•ì œ ìš”ì†Œ ê°œìˆ˜: {len(siblings)}ê°œ")
                for sibling in siblings[:5]:
                    sibling_text = sibling.text.strip()
                    if sibling_text and len(sibling_text) < 20:
                        print(f"    - {sibling.tag_name}: '{sibling_text}'")

                # JavaScriptë¡œ ë” ì •í™•í•œ êµ¬ì¡° íŒŒì•…
                structure = driver.execute_script("""
                    var elem = arguments[0];
                    var parent = elem.parentElement;
                    var result = {
                        parentTag: parent.tagName,
                        parentClass: parent.className,
                        parentId: parent.id,
                        siblingCount: parent.children.length,
                        textNodes: []
                    };

                    // ê°™ì€ ë¶€ëª¨ì˜ ëª¨ë“  ìì‹ í…ìŠ¤íŠ¸
                    for(var i = 0; i < parent.children.length; i++) {
                        var text = parent.children[i].innerText || parent.children[i].textContent;
                        if(text && text.trim()) {
                            result.textNodes.push(text.trim());
                        }
                    }

                    return result;
                """, checkbox)

                print(f"\n  JavaScript ë¶„ì„:")
                print(f"    ë¶€ëª¨ í´ë˜ìŠ¤: {structure['parentClass']}")
                print(f"    í…ìŠ¤íŠ¸ ë…¸ë“œë“¤: {structure['textNodes'][:5]}")

            except Exception as e:
                print(f"  ë¶„ì„ ì˜¤ë¥˜: {str(e)}")

        # íŒ¨í„´ ì°¾ê¸°
        print(f"\n{Fore.GREEN}=== íŒ¨í„´ ë¶„ì„ ==={Style.RESET_ALL}")

        # ëª¨ë“  ì²´í¬ë°•ìŠ¤ì—ì„œ ì´ë¦„ ìœ„ì¹˜ íŒ¨í„´ ì°¾ê¸°
        name_positions = {}
        for checkbox in checkboxes[:20]:
            try:
                parent = checkbox.find_element(By.XPATH, "..")
                siblings = parent.find_elements(By.XPATH, "../*")

                for idx, sibling in enumerate(siblings):
                    text = sibling.text.strip()
                    # 2-5ê¸€ì í•œê¸€ì´ë©´ ì´ë¦„ì¼ ê°€ëŠ¥ì„±
                    if 2 <= len(text) <= 5 and any('ê°€' <= c <= 'í£' for c in text):
                        if idx not in name_positions:
