        print("\nì´ë¦„ì´ ìˆì„ ê°€ëŠ¥ì„±ì´ ë†’ì€ ìœ„ì¹˜:")
        for pos, names in sorted(name_positions.items()):
            print(f"  ìœ„ì¹˜ {pos}: {names[:5]}")

        print(f"\n{Fore.CYAN}ë¶„ì„ ì™„ë£Œ!{Style.RESET_ALL}")
        print("\nì œì•ˆ: ì²´í¬ë°•ìŠ¤ì™€ ê°™ì€ í–‰/ë¶€ëª¨ì˜ íŠ¹ì • ìœ„ì¹˜ì—ì„œë§Œ í…ìŠ¤íŠ¸ë¥¼ ì¶”ì¶œí•˜ì„¸ìš”.")

    except Exception as e:
